import React, {Component} from 'react';

/* estilos en linea */
const styles = {
  height: '200px',
  background: 'gold',
  padding: '1em',
  boxSizing: 'border-box'
}

class App extends Component {
  state = {
    x: 0,
    y: 0
  }
  manejador = (event) =>{
    this.setState({
      x: event.clientX,
      y: event.clientY
    })
  }
  render () {
    return (
      <div 
        style={styles}
        onMouseMove={this.manejador}
      >
        <div>
          X: { this.state.x }
          Y: { this.state.y }
        </div>
      </div>
    )
  }
}

export default App;
