/* Manejador de eventos de input */
class App extends Component {
  state = {
    text: '',
    evento: ''
  }
  manejador = (event) =>{
    this.setState({
      text: event.target.value,
      evento: event.type
    })
  }
  render(){
    return (
      <div>
        <input 
          type="text"
          onChange= {this.manejador}
        />
        <h1>
          {this.state.text}
        </h1>
        <h2>
          {this.state.evento}
        </h2>
      </div>
    )
  }
}


export default App;
