import React, {Component} from 'react';

class Contador extends Component {
  state = {
    clicks: 0
  }

  add = () =>{
    this.setState((state) =>({
        clicks: state.clicks + 1
    }))
  }
  render() {
    return(
      <button onClick = {this.add}>
        Clicks: ({this.state.clicks})
      </button>
    )
  }
}
const App = () =>{
    return(
      <div>
        <Contador/>
      </div>
    )
}

export default App;
