/* Introduccion a la prop children */
const Title = (props) =>{
  const styles = {
    padding: '0.3em',
    color: '#FFF',
    background: props.uiColor,
    borderRadius: '0.3em',
    textAlign: 'center',
    fontSize: '50px'
  }

  return(
      <h1 style={styles}>{props.text}</h1>
  )
}
class App extends Component{
  state = {
    uiColor: 'purple',
  }
  render() {
    return(
      <div>
        <Title uiColor={this.state.uiColor} text='Alberto Rules'/>
      </div>
    )
  }
}
export default App;
