/* Inyectando HTML */
class App extends Component{
  state = {
    marcado: `
      <h1>Inyectando HTML con React</h1>
      <br/>
      <br/>
      <a href="#">Algun Link</a>
    `
  }
  render() {
    return (
      <div>
        { this.state.marcado }
      </div>
    )
  }
}
export default App;
