class Boton extends Component{
  state = {
    dispararError: false
  }

  dispatchError = () =>{
    this.setState({ dispararError: true })
  }

  render() {
    if (this.state.dispararError) {
      throw new Error('Lo siento he fallado')
    }
    return(
      <button onClick={this.dispatchError}>
        Boton con Bugg
      </button>
    )
  }
}
class App extends Component {
  render(){
